
public class Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
		//array - two methods - array is to store the multiple values in single variable.
		//array method one - by using new operator by allocating memory
		int[] arr = new int[3];
		arr[0] = 1;
		arr[1] = 2;	
		arr[2] = 3;
		System.out.println(arr[0]);
		
		//array method two - by using direct2
		int[] arr2 = {1,2,3,4,6,8,10};
		System.out.println(arr2[5]);
		
		//if condition
		if (10>5) {
				System.out.println("10 is greater than 5");
		}
	
		int time =  20;
		if (time<18) {
	             System.out.println("Good day");
		}else {
			    System.out.println("Good Evening");
			  		
  }
	
}
}
