
public class Variabes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Variables are containers for storing data values.
		
		int mynum = 5;
		String website = "raja";
        char letter ='r';
        double decimal = 3.33;
        boolean mycard = true;
        int num = 10;
        
        System.out.println(mynum + "is the integer value stored in the mynum variable");
        System.out.println(website + "is the string value stored in the website variable");
        System.out.println(letter + "is the character value stored in the letter variable");
        System.out.println(decimal+ "is the decimal value stored in the decimal variable");
        System.out.println(mycard + "is the boolean value stored in the mycard variable");
        System.out.println(num);
	}

}

